module Werk
  module Devise
    class ConfigGenerator < Rails::Generators::Base
      def add_gems
        append_to_file 'Gemfile', "gem 'bcrypt'\ngem 'devise'"
      end

      def set_action_mailer
        inject_into_file "config/environments/development.rb", before: /end\Z/ do
          "\n  # generated by werk:devise:config\n  config.action_mailer.default_url_options = { host: 'localhost', port: 3000 }\n"
        end
      end
    end
  end
end
